def remove_outlier(data):
    new_data = []
    for i in data:
        if i < -5:
            pass
        else:
            new_data.append(i)
    return new_data

def convert_to_marker_pos(action):
    return_list = []
    i = 0
    for entry in action:
        if entry == 1:
            return_list.append(i)
        i += 1
    return return_list


